-- ----------------------------
-- Table structure for worklist
-- ----------------------------
DROP TABLE IF EXISTS `worklist`;
CREATE TABLE `worklist`  (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '工作id（主键）',
  `workname` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '工作名',
  `worknumber` int NULL DEFAULT NULL COMMENT '招聘人数',
  `postcompany` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '发布公司',
  `experince` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '工作经验要求',
  `location` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '工作地址',
  `salary` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '薪水',
  `worktime` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '工作时间',
  `worktype` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '工作类型',
  `workdetail` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '工作描述',
  `post_people` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '发布者',
  `workrequest` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '工作要求',
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `companyname`(`postcompany` ASC) USING BTREE,
  CONSTRAINT `companyname` FOREIGN KEY (`postcompany`) REFERENCES `company` (`companyname`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE = InnoDB AUTO_INCREMENT = 53 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of worklist
-- ----------------------------
INSERT INTO `worklist` VALUES (38, 'java开发工程师', 10, '华为', '0', '深圳', '10000', '8小时', '互联网', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">【岗位要求】</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、熟练使用Java语言进行项目开发，精通主流的Web开源框架，Struts2/ Spring、spring mvc、hebernate、ibatis或 mybatis等；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、熟练使用Oracle、MySql、MS SQL Server等主流数据库；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、熟悉HTML、CSS，掌握JS主流框架VUE；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4、 良好的学习能力、沟通能力、理解能力及文档编写能力，具有团队合作精神。</span></p>', '2306732673@qq.com', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">【岗位职责】</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、根据项目需求，完成软件的设计、开发、测试、修改Bug等工作。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、编写测试用例，编写系统模块设计文档。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、维护业务系统，对服务器进行日常维护</span><br/></p>');
INSERT INTO `worklist` VALUES (39, '华为软件工程师', 21, '华为', '3', '广州', '15000', '8小时', '互联网', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">岗位要求</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">（1）计算机、软件、通信等相关专业本科及以上学历，<span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">毕业时间：2023.1.1~2023.12.30日毕业的国内应届学生；</span></span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">（2）热爱编程，基础扎实，熟悉掌握但不限于Java/C++/C/Python/JS/HTML/GO等编程语言中的一种或多种，有良好的编程习惯；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">（3）对数据结构、算法有一定了解，热爱编码；</span></p><p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">千万别走开！！！！！！！！！</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">一年后有公派出国机会！！！！！！</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">提供多元化培养，1对1资深导师助力快速转型和成长，提升你的个人能力。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">发展空间大！团队氛围超赞！</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">每周一次的下午茶、健身走、羽毛球/乒乓球/篮球活动等你来！</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">六险一金、夜宵、免费班车、福利补贴超全！</span></p>', '2306732673@qq.com', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">岗位描述：</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\"> &nbsp; 在这里，你将从事物联网IoT、云计算、边缘计算、AI&amp;机器学习等的设计开发，接触最前沿的产品和软件技术，参与华为产品的软件研发工作；</span></p>');
INSERT INTO `worklist` VALUES (41, '软件开发工程师(C/C++)', 10, '华为', '3', '上海', '12000', '8小时', '互联网', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">【任职要求】</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">业务技能要求：</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、至少精通C/C++/JAVA等一门编程语言,能够熟练运用完成开发工作。熟悉常见的数据结构和算法，熟悉设计模式。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、具备较强的分析问题的思路和解决问题的能力,对技术有激情，喜欢钻研，有较强的独立、主动的学习能力。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、具备较强的责任心，做事认真细致，沟通能力佳。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">专业知识要求：</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4、有大型应用软件微服务化、云化相关工作经验</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">职位招聘100-175人</span></p>', '2306732673@qq.com', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">华为创立于1987年，是全球领先的ICT（信息与通信）基础设施和智能终端提供商，我们致力于把数字世界带入每个人、每个家庭、每个组织，构建万物互联的智能世界：让无处不在的联接，成为人人平等的权利；为世界提供最强算力，让云无处不在，让智能无所不及；所有的行业和组织，因强大的数字平台而变得敏捷、高效、生机勃勃；通过AI重新定义体验，让消费者在家居、办公、出行等全场景获得极致的个性化体验。目前华为约有19.4万员工，业务遍及170多个国家和地区，服务30多亿人口。</span></p>');
INSERT INTO `worklist` VALUES (42, '华为OD招聘', 100, '华为', '3', '深圳', '15000', '8小时', '互联网', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">招聘岗位：</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">软件开发工程师(java) </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1. 统招本科学历，一年及以上相关经验；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2. JAVA基础扎实，熟悉JVM工作原理并掌握常见性能调优方法；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3. 熟悉Spring Cloud微服务架构以及Redis、MQ、APl、Docker、K8S等常用相关技术；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4. 具备良好的文档编制习惯和代码书写规范。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">软件开发工程师(前端)</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1 统招本科以上学历，一年及以上相关经验；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2. 熟练使用JavaScript (ESS, ES6) , 熟恶vue、React前端框架并有实际的开发经验；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3. 具备良好的学习能力，沟通能力优秀，具有良好的团队协作精神。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">软件测试工程师</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1. 统招本科以上学历，一年及以上相关经验；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2. 熟悉自动化测试理论并有相关测试经验.，能主导开展白动化专项测试至少热悉掌握一门开发语言，并运用到自动化项目中；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3. 理解沟通能力强，工作积极主动，有团队精神。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">大数据开发工程师</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1. 统招本科以上学历，一年及以上相关经验；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2. 熟悉flink、spark、 hadoop、 hbase、hive等分布式计算技术，理解其运行机制和体系结构；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3.一年以上大型数据仓库架构和建模经验，熟悉大型互联网企业仓库架构解决方案者优先。</span></p>', '2306732673@qq.com', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">华为OD招聘：能接受异地办公 &nbsp;薪酬丰厚（年薪30-50具体看能力上不封顶）</span></p>');
INSERT INTO `worklist` VALUES (43, '软件开发', 20, '华为', '0', '深圳', '15000', '8小时', '互联网', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1.负责华为研发工具链钱后端开发，包括需求分析，架构设计和代码实现；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2.本科以上学历，扎实的计算机专业基本功；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2.精通java,C++,python或者java SCript语言其中一种；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4.具备较强的推进能力和责任心，优秀的沟通能力和团队精神，热爱技术工作。</span></p>', '2306732673@qq.com', '<ul class=\"job-keyword-list list-paddingleft-2\" style=\"list-style-type: none;\"><li><p>后端工程师</p></li><li><p>计算机/软件工程相关经验</p></li><li><p>Java开发经验</p></li><li><p>分布式系统开发经验</p></li></ul>');
INSERT INTO `worklist` VALUES (44, '云服务开发工程师', 10, '华为', '3', '长沙', '10000', '8小时', '互联网', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\"> 【岗位需求】</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、本科及以上学历；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、熟练掌握Java//Python语言之一，掌握IO、多线程、并发、网络等编程原理；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、熟悉微服务架构理论知识，熟悉公有云和Cloud Native系统架构，熟悉容器体系架构，具备K8S、Docker等容器技术栈软件开发相关经验者优先；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4、熟练掌握Redis、MySQL、Kafka等常用互联网中间件；具有音视频云服务行业工作经验者优先，具备DevOps互联网项目开发经验者优先。</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">5、善于沟通，具备优秀的技术与业务结合能力，善于发现问题、解决问题, 具备较高的质量意识，具备良好的分析和解决问题的能力，具备较强的钻研精神和持续学习意愿，对负有挑战性的工作充满热情。</span></p>', '2306732673@qq.com', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">【岗位职责】</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、负责云通信产品的设计和开发工作，依托公有云技术，打造极致体验、一点接入服务全球的通信云服务，使能物流快递、出行商旅、房产中介等千行百业；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、采用Devops的开发模式高质量完成云服务产品特性设计及核心代码分析与开发，以及模块设计代码看护；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、完成从客户需求到软件产品定义，架构设计，开发实现，再到上线运维等产品寿命周期中的各个环节；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4、创造性解决产品在实现过程中的技术难题，应用前沿技术提高产品核心竞争力，如：服务性能优化，产品调优，引导团队编码能力提升与第三方件的能力提升。</span></p>');
INSERT INTO `worklist` VALUES (45, '陈列主管', 10, '小米', '0', '广东', '10000', '8小时', '市场/传媒', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">任职要求： </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、本科及以上学历，艺术、设计、美术、广告等相关专业； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、有快消零售、3C等相关行业陈列经验者优先，熟悉零售终端及市场推广运作； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、有较强的审美能力，美术表达能力和色彩感；</span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\"> 4、具有一定的品牌意识，思路清晰，反应敏捷，具有较强的分析能力； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">5、能够执行终端店面的货品陈列工作； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">6、有较强的语言表达能力和沟通能力，学习能力强，能迅速融入企业文化；</span></p>', '2259410812@qq.com', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">职责描述： </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">1、执行小米门店陈列规范，监督、调整、维护店铺陈列形象； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">2、组织、执行、实施店铺日常/换季/翻新等陈列工作； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">3、协助店铺陈列人员及店铺销售人员的陈列培训； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">4、分析跟进规范落地执行的效果，持续优化和改善； </span><br/><span style=\"color: rgb(51, 51, 51); font-family: arial, verdana, helvetica, &quot;PingFang SC&quot;, &quot;HanHei SC&quot;, STHeitiSC-Light, &quot;Microsoft Yahei&quot;, sans-serif; font-size: 15px; text-align: justify; white-space: pre-wrap; background-color: rgb(255, 255, 255);\">5、对各个店铺陈列做统一指导，提升店铺销售业绩；</span></p>');
